mapper.update:

update user_info
set user_name = #{user_name}, user_phone = #{user_phone}, user_password = #{user_password}
where user_seq = #{user_seq}

mapper.select:
select * from order_list where user_email=#{user_email} 


		where to_char(order_date, 'yyyy-MM-dd') between #{start_history} and #{end_history}


order by order_seq desc
